//import axion from "axios";
import Axios from "axios";
import {
  GET_ERRORS,
  GET_BACKLOG,
  GET_PROJECT_TASK,
  DELETE_PROJECT_TASK
} from "./types";

export const addProjectTask = (
  backlog_id,
  project_task,
  history
) => async dispatch => {
  try {
    await Axios.post(`/api/backlog/${backlog_id}`, project_task);
    history.push(`/projectBoard/${backlog_id}`);
    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const getBacklog = backlog_id => async dispatch => {
  try {
    const res = await Axios.get(`/api/backlog/${backlog_id}`);
    dispatch({
      type: GET_BACKLOG,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: { error: "Not Found" }
    });
  }
};

export const getProjectTask = (
  backlog_id,
  projectSequence,
  history
) => async dispatch => {
  try {
    const res = await Axios.get(
      `/api/backlog/${backlog_id}/${projectSequence}`
    );
    dispatch({
      type: GET_PROJECT_TASK,
      payload: res.data
    });
  } catch (err) {
    history.push("/dashboard");
  }
};

export const updateProjectTask = (
  backlog_id,
  pTSequence,
  project_task,
  history
) => async dispatch => {
  try {
    await Axios.patch(`/api/backlog/${backlog_id}/${pTSequence}`, project_task);
    history.push(`/projectBoard/${backlog_id}`);
    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const deleteProjectTask = (backlog_id, pTSequence) => async dispatch => {
  if (
    window.confirm(
      `You are deleting project task ${pTSequence}, this action cannot be undone`
    )
  ) {
    await Axios.delete(`/api/backlog/${backlog_id}/${pTSequence}`);
    dispatch({
      type: DELETE_PROJECT_TASK,
      payload: pTSequence
    });
  }
};
