import React, { Component } from "react";
import ProjectTask from "../components/ProjectBoard/ProjectTasks/ProjectTask";

class Backlogdrag {
  solve() {
    const { project_tasks_prop } = this.props;

    var tasks = [];
    console.log(project_tasks_prop);

    if (typeof project_tasks_prop !== "string") {
      tasks = project_tasks_prop.map(project_task => (
        <ProjectTask key={project_task.id} project_task={project_task} />
      ));
    }
    return tasks;
  }
  /*
  render() {
    const { project_tasks_prop } = this.props;

    //var tasks = [];

    if (typeof project_tasks_prop !== "string") {
      tasks = project_tasks_prop.map(project_task => (
        <ProjectTask key={project_task.id} project_task={project_task} />
      ));
    }

    return tasks;
    
  }*/
}
let ckdrag = new Backlogdrag();
var tasks = ckdrag.solve();
console.log(tasks);

