import React from "react";
import "@atlaskit/css-reset";
import { DragDropContext } from "react-beautiful-dnd";
import styled from "styled-components";
import Column from "./dragcomponents/column";
import ReactDOM from "react-dom";

const Container = styled.div`
  display: flex;
`;

var array_of_objects = [];

function populate() {
  var initialData = {
    tasks: {},
    columns: {
      "column-1": { id: "column-1", title: "TO_DO", taskIds: [] },
      "column-2": { id: "column-2", title: "IN_PROGRESS", taskIds: [] },
      "column-3": { id: "column-3", title: "DONE", taskIds: [] }
    }
  };

  for (let i = 0; i < array_of_objects.length; i++) {
    var key1 = "task-" + (i + 1);
    initialData["tasks"][key1] = {};
    initialData["tasks"][key1]["id"] = key1;
    initialData["tasks"][key1]["content"] = array_of_objects[i];
  }

  //Second Part => Updating Colums [obj1, obj2]
  for (let i = 0; i < array_of_objects.length; i++) {
    if (array_of_objects[i].props.project_task.status === "TO_DO") {
      initialData["columns"]["column-1"]["taskIds"].push(array_of_objects[i]);
    }
    if (array_of_objects[i].props.project_task.status === "IN_PROGRESS") {
      initialData["columns"]["column-2"]["taskIds"].push(array_of_objects[i]);
    }
    if (array_of_objects[i].props.project_task.status === "DONE") {
      initialData["columns"]["column-3"]["taskIds"].push(array_of_objects[i]);
    }
  }

  return initialData;
}

//[object1,object2,object3] => Assuming it isn't empty
//Update the tasks with objects
//Update the colums with the task-ids

//First Part => Updating tasks [obj1,obj2]

class App extends React.Component {
  state = populate();

  //
  onDragEnd = result => {
    const { destination, source, draggableId } = result; //origin, source, item to move were intialized by onDragEnd's prop

    //return if not destination
    if (!destination) {
      return;
    }

    //return if the item isn't moved
    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const start = this.state.columns[source.droppableId]; //from column
    const finish = this.state.columns[destination.droppableId]; //to column

    if (start === finish) {
      const newTaskIds = Array.from(start.taskIds);
      newTaskIds.splice(source.index, 1);
      newTaskIds.splice(destination.index, 0, draggableId);

      const newColumn = {
        ...start,
        taskIds: newTaskIds
      };

      const newState = {
        ...this.state,
        columns: {
          ...this.state.columns,
          [newColumn.id]: newColumn
        }
      };

      this.setState(newState);
      return;
    }

    // Moving from one list to another
    const startTaskIds = Array.from(start.taskIds);
    startTaskIds.splice(source.index, 1);
    const newStart = {
      ...start,
      taskIds: startTaskIds
    };

    const finishTaskIds = Array.from(finish.taskIds);
    finishTaskIds.splice(destination.index, 0, draggableId);
    const newFinish = {
      ...finish,
      taskIds: finishTaskIds
    };

    const newState = {
      ...this.state,
      columns: {
        ...this.state.columns,
        [newStart.id]: newStart,
        [newFinish.id]: newFinish
      }
    };
    this.setState(newState);
  };

  render() {
    const { project_tasks_prop } = this.props;
    console.log();

    var tasks = [];

    if (typeof project_tasks_prop !== "string") {
      tasks = project_tasks_prop.map(project_task => (
        <ProjectTask key={project_task.id} project_task={project_task} />
      ));
    }

    return (
      <DragDropContext onDragEnd={this.onDragEnd}>
        <Container>
          {this.state.columnOrder.map(columnId => {
            const column = this.state.columns[columnId];
            const tasks = column.taskIds.map(
              taskId => this.state.tasks[taskId]
            );

            return <Column key={column.id} column={column} tasks={tasks} />;
          })}
        </Container>
      </DragDropContext>
    );
  }
}

export default App;
